mapper.select:
select 

from statistics
where 1=1

statistics_id = #{id,jdbcType=INTEGER}


  and user_id = #{userId,jdbcType=INTEGER}


  and product_id = #{productId,jdbcType=INTEGER}


  and product_type = #{productType,jdbcType=CHAR}


  and create_date = #{createDate,jdbcType=TIMESTAMP}

mapper.insert:
