mapper.select:
select 

from statistics
where 1=1

statistics_id = #{id,jdbcType=INTEGER}


  and user_id = #{userId,jdbcType=INTEGER}


  and commenttoid = #{commentToId,jdbcType=INTEGER}


  and type = #{type,jdbcType=CHAR}


  and create_date = #{createDate,jdbcType=TIMESTAMP}

mapper.insert:
insert into statistics (statistics_id, user_id, 
	commenttoid, type, create_date)
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{commentToId,jdbcType=INTEGER}, 
  #{type,jdbcType=CHAR}, #{createDate,jdbcType=TIMESTAMP}
  )

