mapper.select:
select  from t_category

	
		and cate_id = #{cateId,jdbcType=BIGINT}
	
	
		and cate_name = #{cateName,jdbcType=VARCHAR}
	
	
		and cate_pid = #{catePid,jdbcType=BIGINT}
	
	
		and cate_level = #{cateLevel,jdbcType=INTEGER}
	
	
		and cate_is_delete = #{cateIsDelete,jdbcType=INTEGER}
	
	
		and cate_create_time = #{cateCreateTime,jdbcType=TIMESTAMP}
	
	
		limit ${(pageCount-1)*pageSize }, #{pageSize }

