mapper.select:
select  from t_comment

	
		and comm_id = #{commId,jdbcType=BIGINT}
	
	
		and comm_content = #{commContent,jdbcType=VARCHAR}
	
	
		and res_id = #{resId,jdbcType=BIGINT}
	
	
		and user_id = #{userId,jdbcType=BIGINT}
	
	
		and comm_is_show = #{commIsShow,jdbcType=INTEGER}
	
	
		and comm_is_delete = #{commIsDelete,jdbcType=INTEGER}
	
	
		and create_id = #{createId,jdbcType=BIGINT}
	
	
		and comm_create_time = #{commCreateTime,jdbcType=TIMESTAMP}
	
	
		and comm_update_time = #{commUpdateTime,jdbcType=TIMESTAMP}

