mapper.select:
select  from t_resource

	
		and res_id = #{resId,jdbcType=BIGINT}
	
	
		and res_name = #{resName,jdbcType=VARCHAR}
	
	
		and res_info = #{resInfo,jdbcType=VARCHAR}
	
	
		and res_type = #{resType,jdbcType=INTEGER}
	
	
		and res_url = #{resUrl,jdbcType=VARCHAR}
	
	
		and user_id = #{userId,jdbcType=BIGINT}
	
	
		and cate_id = #{cateId,jdbcType=BIGINT}
	
	
		and res_integral = #{resIntegral,jdbcType=BIGINT}
	
	
		and res_audit_type = #{resAuditType,jdbcType=INTEGER}
	
	
		and res_is_show = #{resIsShow,jdbcType=INTEGER}
	
	
		and res_is_delete = #{resIsDelete,jdbcType=INTEGER}
	
	
		res_cover_img = #{resCoverImg,jdbcType=VARCHAR},
	
	
		res_homework = #{resHomework,jdbcType=VARCHAR},
	
	
		and res_create_time = #{resCreateTime,jdbcType=TIMESTAMP}
	
	
		and res_update_time = #{resUpdateTime,jdbcType=TIMESTAMP}
	


	order by ${sortFields } 
	${sort }


	limit ${(pageCount-1)*pageSize }, #{pageSize }

select count(1) from t_resource

	
		and res_id = #{resId,jdbcType=BIGINT}
	
	
